mapper.select:
SELECT COUNT(*) FROM V_BOARD

	
		
		
			
				AND BOARD_TITLE LIKE #{sv}
			
			
				AND BOARD_CONTENT LIKE #{sv}
			
			
				AND (BOARD_TITLE LIKE #{sv}
				OR BOARD_CONTENT LIKE #{sv})
			
		
		
			AND CATEGORY_NM IN
			
				#{item}
			
		
	
	AND BOARD_TYPE = 1

ORDER BY BOARD_NO DESC

SELECT BOARD_NO, BOARD_TITLE, BOARD_COUNT,
BOARD_MODIFY_DT, MEMBER_ID, CATEGORY_NM
FROM V_BOARD

	
		
		
			
				AND BOARD_TITLE LIKE #{sv}
			
			
				AND BOARD_CONTENT LIKE #{sv}
			
			
				AND (BOARD_TITLE LIKE #{sv}
				OR BOARD_CONTENT LIKE #{sv})
			
		
		
			AND CATEGORY_NM IN
			
				#{item}
			
		
	
	AND BOARD_TYPE = 1

ORDER BY BOARD_NO DESC

