mapper.select:
		SELECT COUNT(*) FROM NOTICE
		
			
				

				
					
						AND NOTICE_TITLE LIKE #{sv}
					
					
						AND NOTICE_CONTENT LIKI #{sv}
					
					
						AND ( NOTICE_TITLE LIKE #{sv}
						OR NOTICE_CONTENT LIKE
						#{sv})
					
				

			
			AND NOTICE_STATUS = 'Y'

		SELECT NOTICE_NO, NOTICE_TITLE, NOTICE_WRITER,
		NOTICE_COUNT,NOTICE_MODIFY_DT
		FROM NOTICE

		
			
				
				
					
						AND NOTICE_TITLE LIKE #{sv}
					
					
						AND NOTICE_CONTENT LIKI #{sv}
					
					
						AND ( NOTICE_TITLE LIKE #{sv}
						OR NOTICE_CONTENT LIKE
						#{sv})
					
				

			
			AND NOTICE_STATUS = 'Y'
			ORDER BY NOTICE_NO DESC

mapper.update:
UPDATE NOTICE SET
NOTICE_COUNT = NOTICE_COUNT+1
WHERE NOTICE_NO = #{noticeNo}

UPDATE NOTICE SET
NOTICE_STATUS = 'N'
WHERE NOTICE_NO = #{no}

UPDATE NOTICE SET
NOTICE_TITLE = #{noticeTitle}, NOTICE_CONTENT = #{noticeContent}
WHERE NOTICE_NO = #{noticeNo}
